  React.useEffect(() => {
    document.title = `You clicked ${count} times`;

  },[count]);
  React.useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/todos/1')
    .then(response => response.json())
    .then(json => console.log(json))
