function mountState(
  initialState: (() => S) | S,
): [S, Dispatch>] {

  //创建一个hook,改变workInProgressHook指针指向
  const hook = mountWorkInProgressHook();
  //initialState是函数就调用它
  if (typeof initialState === 'function') {
    initialState = initialState();
  }
  //state初始化
  hook.memoizedState = hook.baseState = initialState;
  //queue初始化
  const queue = (hook.queue = {
    last: null,//最近的一次更新
    dispatch: null,
    lastRenderedReducer: basicStateReducer,// useState使用基础reducer
    lastRenderedState: (initialState: any),
  });
  // 触发器绑定当前fiber和queue
  const dispatch: Dispatch,
  > = (queue.dispatch = (dispatchAction.bind(
    null,
    ((currentlyRenderingFiber: any): Fiber),
    queue,
  ): any));
  // 返回初始state和触发器
  return [hook.memoizedState, dispatch];
}
可以看到dispatch就是dispatchAction绑定了对应的Fiber和queue这两个参数。
function basicStateReducer(state: S, action: BasicStateAction): S {
